/*    Current dashboards and their questions    https://discourse.metabase.com/t/metabase-metadata-sql/3688*/SELECT    report_dashboard.id AS dashboard_id,    report_dashboard.name AS dashboard_name,    report_card.id AS question_id,    report_card.name AS question_name,    report_card.display AS question_display,    CONCAT(cu_d.first_name, ' ', cu_d.last_name) AS dashboard_creator,    CONCAT(cu_c.first_name, ' ', cu_c.last_name) AS question_creatorFROM report_dashboard    INNER JOIN report_dashboardcard        ON report_dashboard.id = report_dashboardcard.dashboard_id    INNER JOIN report_card        ON report_card.id = report_dashboardcard.card_id    LEFT JOIN core_user AS cu_d        ON report_dashboard.creator_id = cu_d.id    LEFT JOIN core_user AS cu_c        ON report_card.creator_id = cu_c.idWHERE 1=1    AND report_dashboard.archived = 0    AND report_card.archived = 0    [[AND {{dashboard_name}}]]    [[AND {{question_name}}]]    [[AND {{dashboard_id}}]]    [[AND {{question_id}}]];